<#PSScriptInfo
.SYNOPSIS
    Script for Intune Remediation to detect if Restart Pending or last restart is older than x days

.DESCRIPTION
    This script will detect if Restart is needed and toast propting the user to restart computer
    It will log the transcript and write it to eventlog, The transcript text will also be returned to Intune

.NOTES
    .AUTHOR         Mr Tbone Granheden @MrTbone_se 
    .COMPANYNAME    Coligo AB @coligoAB
    .COPYRIGHT      Feel free to use this, but would be grateful if my name is mentioned in notes

.RELESENOTES
    1.0 Initial version
#>

#region ------------------------------------------------[Set script requirements]------------------------------------------------
#Requires -Version 4.0
#endregion

#region -------------------------------------------------[Modifiable Parameters]-------------------------------------------------
[String]$RemediationName            = "Restart"     # Used for Eventlog
[int]$DaysBetweenRestart            = 7             # Days between required Restart
[string]$Logpath                    = "$($env:TEMP)"    # Path to log transcript
# Toast Notification text
[string]$global:ToastTitle          = "Restart Required"
[String]$global:ToastAuthor         = "IT ServiceDesk"
[string]$global:ToastHeader         = "TIP: We recommend you to restart your computer at least once a week"
[string]$global:ToastBody           = "Restart normally takes a few minutes, but some lager updates can take up to 15 minutes."
[string]$global:ToastFooter         = "To ensure the stability and proper functioning of your system, consider restarting your device very soon."
[String]$global:ToastAppID          = "RestartToast"
[Bool]$global:ShowDismissButton     = $False
[Bool]$global:ShowRestartButton     = $true
[Bool]$global:ShowSnoozeButton      = $true
[Bool]$global:ShowHeroImage         = $true
[Bool]$global:ShowLogoImage         = $true
#Toast Images
$Global:ToastHeroImage_Base64 = ""
$Global:ToastLogoImage_Base64 = ""
#$Global:ToastLogoImage_Base64 = ""
$Global:ToastHeroImage_URL          = $null #"https://csi0101.blob.core.windows.net/img/hommer3.gif" #set to $null to use Base64 image
$Global:ToastLogoImage_URL          = $null #"https://csi0101.blob.core.windows.net/img/hommer3.gif" #set to $null to use Base64 image
#endregion

#region --------------------------------------------------[Static Variables]-----------------------------------------------------
#Declare variables
[string]$Transcript = $null
[string]$EventType  = $null
[int32]$eventID     = $null
#set defaults
[Bool]$Detected     = $False
#set Eventsource and Logfile
[string]$Logfile    = "$($Logpath)\Detect-$($RemediationName).log"
[string]$eventsource="Detect-$($RemediationName)"
#endregion

#region --------------------------------------------[Import Modules and Extensions]----------------------------------------------
#endregion

#region ------------------------------------------------------[Functions]--------------------------------------------------------
Function Detect-RestartPending {
    Param(
        [int]$DaysBetweenRestart
        
        )
    Begin {
        $pendingRestartKeys = @( 
            "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\WindowsUpdate\Auto Update\RebootRequired",
            "HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\PendingFileRenameOperations",
            "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Component Based Servicing\RebootPending",
            "HKLM:\SOFTWARE\Wow6432Node\Microsoft\Windows\CurrentVersion\Component Based Servicing\RebootPending",
            "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\WindowsUpdate\Auto Update\RebootRequired"
        )
        $LastEventRestart        = $null
        $LastCIMRestart          = $null
        $LastRestart             = $null
        $DaysSinceRestart        = $null            
    }
    Process {
#Check for pending Restart
        foreach ($key in $pendingRestartKeys) {
            if (Test-Path $key) {
                Write-verbose "Pending Restart was found - Need to Restart" -verbose
                $pendingRestart = $true
            }
        }
        if (!$pendingRestart){Write-verbose "No Pending Restart was found" -verbose}
#Check time since last Restart
        [datetime]$LastEventRestart = (Get-WinEvent -ProviderName EventLog | Where-Object {$_.Id -eq 6005 -or $_.Id -eq 6006} | Select-Object -First 1 TimeCreated).timecreated
        if ($LastEventRestart -and ($error.count -eq 0)){
            Write-verbose "Enumerated last Restart from eventlog to $($LastEventRestart)" -verbose}
        else{Write-warning "Failed to enumerate last Restart from eventlog with error:$($error.Exception.Message)";$error.Clear()}
        [datetime]$LastCIMRestart = Get-CimInstance -ClassName Win32_OperatingSystem -ErrorAction silentlycontinue| Select-Object -ExpandProperty LastBootUpTime
        if ($LastCIMRestart -and ($error.count -eq 0)){
            Write-verbose "Enumerated last Restart from CIM to $($LastCIMRestart)" -verbose}
        else{Write-warning "Failed to enumerate last Restart from CIM with error:$($error.Exception.Message)";$error.Clear()}
        if ($LastCIMRestart -and $LastEventRestart) {
            [datetime]$LastRestart = ($LastCIMRestart, $LastEventRestart) | Measure-Object -Maximum | Select-Object -ExpandProperty Maximum
        }
        elseif ($LastCIMRestart) {
            [datetime]$LastRestart = $LastCIMRestart
        }
        elseif ($LastEventRestart) {
            [datetime]$LastRestart = $LastEventRestart
        }
        else {
            Write-Error "Failed to enumerate last Restart from CIM and eventlog, set to interval"
            [datetime]$LastRestart = (Get-Date).AddDays($DaysBetweenRestart)
        }
        $DaysSinceRestart = [math]::Floor(([DateTime]::Now - $LastRestart).TotalDays)
        Write-Verbose "Days since last Restart: $DaysSinceRestart and required interval is $($DaysBetweenRestart)" -verbose
        if ($pendingRestart){
            $ToastMessage = "Your computer has a pending Restart after a Windows update or Software update and needs to Restart to maintain stability."
            display-Toast $ToastMessage |Out-Null
            return $true}
        elseif($DaysSinceRestart -gt $DaysBetweenRestart){
            $ToastMessage = "Your computer has not been Restarted in $($DaysSinceRestart) days, our recommended interval is to Restart every $($DaysBetweenRestart) days."
            display-Toast $ToastMessage |Out-Null
            return $true}
        else{
            return $true
        }
    }
    End {}
}
Function display-Toast {
    Param(
        [string]$ToastMessage
        )
    Begin {}
    Process {
#Prepare Toast Hero Image
    if ($global:ShowHeroImage){
        $ToastHeroImage = "$env:TEMP\ToastHeroImage.png"
        if ($null -eq $Global:ToastHeroImage_URL){
            [byte[]]$Bytes = [convert]::FromBase64String($Global:ToastHeroImage_Base64)
            [System.IO.File]::WriteAllBytes($ToastHeroImage,$Bytes)
        }
        else{Invoke-WebRequest -Uri $Global:ToastHeroImage_URL -OutFile $ToastHeroImage}
        #$ToastHeroImageXML = '<image placement="hero" src="$ToastHeroImage"/>'
    }
#Prepare Toast Logo Image
    if ($global:ShowLogoImage){
        $ToastLogoImage = "$env:TEMP\ToastLogoImage.png"
        if ($null -eq $Global:ToastLogoImage_URL){
            [byte[]]$Bytes = [convert]::FromBase64String($Global:ToastLogoImage_Base64)
            [System.IO.File]::WriteAllBytes($ToastLogoImage,$Bytes)
        }
        else{Invoke-WebRequest -Uri $Global:ToastLogoImage_URL -OutFile $ToastLogoImage}
        #$ToastLogoImageXML = '<image id="1" placement="appLogoOverride" hint-crop="circle" src="$ToastLogoImage"/>'
    }
#Prepare Restart Button    
    if ($global:ShowRestartButton){
        New-PSDrive -Name HKCR -PSProvider Registry -Root HKEY_CLASSES_ROOT -erroraction silentlycontinue | out-null
        $ProtocolHandler = get-item 'HKCR:\ToastRestart' -erroraction 'silentlycontinue'
        if (!$ProtocolHandler) {
            #create handler for Restart
            New-item 'HKCR:\ToastRestart' -force
            set-itemproperty 'HKCR:\ToastRestart' -name '(DEFAULT)' -value 'url:ToastRestart' -force
            set-itemproperty 'HKCR:\ToastRestart' -name 'URL Protocol' -value '' -force
            new-itemproperty -path 'HKCR:\ToastRestart' -propertytype dword -name 'EditFlags' -value 2162688
            New-item 'HKCR:\ToastRestart\Shell\Open\command' -force
            set-itemproperty 'HKCR:\ToastRestart\Shell\Open\command' -name '(DEFAULT)' -value 'C:\Windows\System32\shutdown.exe -r -t 00' -force
        }
        $RestartButtonXML = '<action activationType="protocol" arguments="ToastRestart:" content="Restart now" />'
    }
#Prepare Snooze Button    
    if ($global:ShowSnoozeButton){
        $SnoozeButtonXML = '
        <input id="SnoozeTimer" type="selection" title="Snooze" defaultInput="30">
        <selection id="30" content="30 Minutes"/>
        <selection id="60" content="1 Hour"/>
        <selection id="120" content="2 Hours"/>
        <selection id="240" content="4 Hours"/>
        <selection id="480" content="8 Hours"/>
        </input>
        <action activationType="system" arguments="snooze" hint-inputId="SnoozeTimer" content="Snooze" id="test-snooze"/>'
    }
#Prepare Dismiss Button
    if ($global:ShowDismissButton){
        $dismissButtonXML = '<action arguments="dismiss" content="Dismiss" activationType="system"/>'
    }
#Create the Toast App in registry
    $CustomToastNotifyRegKey = "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Notifications\Settings\$ToastAppID"
    $CustomHandlerClassRegKey = "HKCU:\Software\Classes\AppUserModelId"
    New-Item -Path $CustomToastNotifyRegKey -Force | Out-Null
    New-ItemProperty -Path $CustomToastNotifyRegKey -Name "ShowInActionCenter" -Value 1 -PropertyType DWORD -Force | Out-Null
    New-Item -Path $CustomHandlerClassRegKey -Name $ToastAppID -Force | Out-Null
    New-ItemProperty -Path $CustomHandlerClassRegKey\$ToastAppID -Name "DisplayName" -Value $global:ToastAuthor -PropertyType String -Force | Out-Null
    New-ItemProperty -Path $CustomHandlerClassRegKey\$ToastAppID -Name "ShowInSettings" -Value 0 -PropertyType DWORD -Force | Out-Null
    
#Load Assemblies for Toast
    [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime] | Out-Null
    [Windows.Data.Xml.Dom.XmlDocument, Windows.Data.Xml.Dom.XmlDocument, ContentType = WindowsRuntime] | Out-Null

#Build XML for Toast 
    [xml]$ToastXMLTemplate = @"
<toast scenario="reminder">
    <visual>
    <binding template="ToastGeneric">
        <image placement="hero" src="$ToastHeroImage"/>
        <image id="1" placement="appLogoOverride" hint-crop="circle" src="$ToastLogoImage"/>
        <text placement="attribution">$global:ToastAuthor</text>
        <text>$global:ToastHeader</text>
        <group>
            <subgroup>
                <text hint-style="title" hint-wrap="true" >$global:ToastTitle</text>
            </subgroup>
        </group>
        <group>
            <subgroup>     
                <text hint-style="body" hint-wrap="true" >$ToastMessage</text>
            </subgroup>
        </group>
        <group>
            <subgroup>     
                <text hint-style="captionsubtle" hint-wrap="true" >$global:ToastBody</text>
            </subgroup>
        </group>
        <group>
            <subgroup>     
                <text hint-style="body" hint-wrap="true" >$global:ToastFooter</text>
            </subgroup>
        </group>
    </binding>
    </visual>
    <actions>
    $SnoozeButtonXML
    $RestartButtonXML
    $dismissButtonXML		
    </actions>
</toast>
"@

#Prepare XML
    $ToastXml = [Windows.Data.Xml.Dom.XmlDocument]::New()
    $ToastXml.LoadXml($ToastXMLTemplate.OuterXml)

#Prepare and Create Toast
    $ToastXMLMessage = [Windows.UI.Notifications.ToastNotification]::New($ToastXML)
    [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($ToastAppID).Show($ToastXMLMessage)
        
    }
    End {}
}
function Write-ToEventlog {
    Param(
        [string]$Logtext,
        [string]$EventSource,
        [int]$EventID,
        [validateset("Information", "Warning", "Error")]$EventType = "Information"
    )
    Begin {}
    Process {
    if (!([System.Diagnostics.EventLog]::SourceExists($EventSource))) {
        New-EventLog -LogName 'Application' -Source $EventSource -ErrorAction ignore | Out-Null
        }
    Write-EventLog -LogName 'Application' -Source $EventSource -EntryType $EventType -EventId $EventID -Message $Logtext -ErrorAction ignore | Out-Null
    }
    End {}
}
#endregion
#region --------------------------------------------------[Script Execution]-----------------------------------------------------
#Start logging
start-transcript -Path $Logfile

#Detect
$Detected = Detect-RestartPending $DaysBetweenRestart

#Get transcript and cleanup the text
Stop-Transcript |out-null
$Transcript = ((Get-Content $LogFile -Raw) -split ([regex]::Escape("**********************")))[-3]

#region -----------------------------------------------------[Detection]---------------------------------------------------------
#Compliant
if ($Detected){
    $EventText =  "Compliant - No need to Remediate `n$($Transcript)";$eventID=10;$EventType="Information"
    Write-ToEventlog $EventText $EventSource $eventID $EventType
    Write-output "$($EventText -replace "`n",", " -replace "`r",", ")" #with no line breaks
    Exit 0
}
#Non Compliant
Else{
    $EventText =  "NON Compliant - Need to Remediate `n$($Transcript)";$eventID=11;$EventType="Warning"
    Write-ToEventlog $EventText $EventSource $eventID $EventType
    Write-output "$($EventText -replace "`n",", " -replace "`r",", ")" #with no line breaks
    Exit 1
}
#endregion